function getEmote(arg) {
	if (emote[arg]) {
		var emoteObj = {
			"MSG_MYSELF": emote[arg][1],
			"MSG_OTHERS": emote[arg][2],
			"MSG_SELF_MYSELF": emote[arg][3],
			"MSG_SELF_OTHERS": emote[arg][4],
			"MSG_TARGET_MYSELF": emote[arg][5],
			"MSG_TARGET": emote[arg][6],
			"MSG_TARGET_OTHERS": emote[arg][7]
		};
		if (emoteObj.MSG_MYSELF == 0) emoteObj.MSG_MYSELF = "";
		if (emoteObj.MSG_OTHERS == 0) emoteObj.MSG_OTHERS = emoteObj.MSG_MYSELF.replace(/\$P/g, "$N");
		if (emoteObj.MSG_SELF_MYSELF == 0) emoteObj.MSG_SELF_MYSELF = emoteObj.MSG_MYSELF;
		if (emoteObj.MSG_SELF_OTHERS == 0) emoteObj.MSG_SELF_OTHERS = emoteObj.MSG_SELF_MYSELF.replace(/\$P/g, "$N");
		if (emoteObj.MSG_TARGET_MYSELF == 0) emoteObj.MSG_TARGET_MYSELF = emoteObj.MSG_MYSELF;
		if (emoteObj.MSG_TARGET == 0) emoteObj.MSG_TARGET = emoteObj.MSG_TARGET_MYSELF.replace(/\$P/g, "$N").replace(/\$n/g, "$p");
		if (emoteObj.MSG_TARGET_OTHERS == 0) emoteObj.MSG_TARGET_OTHERS = emoteObj.MSG_TARGET_MYSELF.replace(/\$P/g, "$N");
		return emoteObj;
	}
	return null;
}

function showEmote(arg) {
	var returnValue = "";
	var counter = 0;
	var key = new Array();
	for (var record in emote)
	{
		key.push(record+"");
	}
	key = key.sort();

	if (!arg) {
		returnValue += "表情動作總匯︰\n";
		for (var i = 0; i < key.length; i++) {
			returnValue += "<span class='emoteRecord" + ((i+1) % 5 == 0?" clear":"") + "'>" + key[i] + "</span>";// + ((i+1) % 5 == 0?"\n":"");
			counter++;
		}
		returnValue += "共有 $count 個 emote。\n";
	}
	else {
		for (var i = 0; i < key.length; i++) {
			if (key[i].indexOf(arg) == 0) {
				var emoteObj = getEmote(key[i]);
				returnValue += "emote名稱：" + key[i] + "\n";
				returnValue += " - " + emoteObj.MSG_MYSELF + "\n";
				returnValue += " - " + emoteObj.MSG_OTHERS + "\n";
				returnValue += " - " + emoteObj.MSG_SELF_MYSELF + "\n";
				returnValue += " - " + emoteObj.MSG_SELF_OTHERS + "\n";
				returnValue += " - " + emoteObj.MSG_TARGET_MYSELF + "\n";
				returnValue += " - " + emoteObj.MSG_TARGET + "\n";
				returnValue += " - " + emoteObj.MSG_TARGET_OTHERS + "\n\n";
				counter++;
			}
		}
		if (counter > 0) {
			returnValue += "共有 $count 個以\"" + arg + "\"開頭的 emote。\n";
		}
		else {
			for (var i = 0; i < key.length; i++) {
				var emoteObj = getEmote(key[i]);
				for (var action in emoteObj) {
					if (emoteObj[action].indexOf(arg) != -1) {
						returnValue += "emote名稱：" + key[i] + "\n";
						returnValue += "動作：" + emoteObj[action] + "\n\n";
						counter++;
						break;
					}
				}
			}
			if (returnValue == "")
				returnValue += "無符合查詢條件的 emote。";
			else
				returnValue += "共有 $count 個包含\"" + arg + "\"的 emote。\n";
		}
	}
	var target = {"name": "(對方)", "gender":""};
	return returnValue
		.replace(/\$count/g, counter)
		.replace(/\$N/g, myChar.name)
		.replace(/\$P/g, "你")
		.replace(/\$n/g, "(對方)")
		.replace(/\$p/g, "(他/她)")
		.replace(/\$S/g, query_self(myChar))
		.replace(/\$s/g, query_self_rude(myChar))
		.replace(/\$C/g, "(自己的呢稱)")
		.replace(/\$c/g, "(對方的呢稱)")
		.replace(/\$R/g, "(對方的尊稱)")
		.replace(/\$r/g, "(對方的辱稱)");
}

function praseEmote(action) {
	var returnValue = "";
	var actions = action.split(" ");
	var target = null;
	if (actions.length > 1) {
		action = actions[0];
		target = actions[1];
		if (!npcUser[target])
			return "沒有這個人。";
		else
			target = npcUser[target];
	}
	if (emote[action]) {
		var emoteObj = getEmote(action);
		returnValue = (target==null?emoteObj.MSG_MYSELF:(target.id==$("#txtMyID").val()?emoteObj.MSG_SELF_MYSELF:emoteObj.MSG_TARGET_MYSELF));
	}
	if (!target) target = {"name": "", "gender":""};
	return returnValue
		.replace(/\$N/g, myChar.name)
		.replace(/\$P/g, (myChar.gender=="F"?"妳":"你"))
		.replace(/\$n/g, target.name)
		.replace(/\$p/g, (target.gender=="F"?"她":"他"))
		.replace(/\$S/g, query_self(myChar))
		.replace(/\$s/g, query_self_rude(myChar))
		.replace(/\$C/g, query_self_close(target))
		.replace(/\$c/g, query_close(target))
		.replace(/\$R/g, query_respect(target))
		.replace(/\$r/g, query_rude(target));
}
